Field.BindOnChange:
var TarikheAgazeAmaliyat = form.fields.TarikheAgazeAmaliyat.value;
var TarikheEtebareEstelam = form.fields.TarikheEtebareEstelam.value;

var TarikheEmrooz = moment().format("YYYY MM DD");

var TarikheMojazeAgazeAmaliyat = moment(moment(TarikheEmrooz).add(180,'days')).format("YYYY MM DD");

if (moment(TarikheAgazeAmaliyat).isValid() && moment(TarikheEtebareEstelam).isValid()) {

    var TarikheAgazeAmaliyatMiladiString = ShamsiToMiladi(parseInt(TarikheAgazeAmaliyat.substr(0, 4)),
        parseInt(TarikheAgazeAmaliyat.substr(5, 2)), parseInt(TarikheAgazeAmaliyat.substr(8, 2)));

    var TarikheEtebareEstelamMiladiString = ShamsiToMiladi(parseInt(TarikheEtebareEstelam.substr(0, 4)),
        parseInt(TarikheEtebareEstelam.substr(5, 2)), parseInt(TarikheEtebareEstelam.substr(8, 2)));


    var TarikheAgazeAmaliyatMiladiDate = moment(TarikheAgazeAmaliyatMiladiString,"YYYY,MM,DD").format("YYYY MM DD");


    var TarikheEtebareEstelamMiladiDate = moment(TarikheEtebareEstelamMiladiString,"YYYY,MM,DD").format("YYYY MM DD");


    if (TarikheEtebareEstelamMiladiDate < TarikheEmrooz || TarikheAgazeAmaliyatMiladiDate < TarikheEmrooz) {

        swal({   title: "خطایی رخ داده است!",   text: "تاریخ ها نمیتوانند روزی قبل از امروز باشند.",
                type: "warning",   showCancelButton: false,   confirmButtonColor: "#DD6B55",
                confirmButtonText: "توضیح بیشتر",   closeOnConfirm: false   ,allowOutsideClick:	true },
            function(){
                var win = window.open("/سایر-خدمات/آموزش", '_blank');
                win.focus();
            });
        return false;
    }
    else if(TarikheMojazeAgazeAmaliyat < TarikheAgazeAmaliyatMiladiDate){
        swal({   title: "خطایی رخ داده است!",   text: "تاریخ آغاز عملیات حداکثر 60 روز بعد میتواند باشد.",
                type: "warning",   showCancelButton: false,   confirmButtonColor: "#DD6B55",
                confirmButtonText: "توضیح بیشتر",   closeOnConfirm: false   ,allowOutsideClick:	true },
            function(){
                var win = window.open("/سایر-خدمات/آموزش", '_blank');
                win.focus();
            });
        return false;
    }
    else if (TarikheAgazeAmaliyatMiladiDate < TarikheEtebareEstelamMiladiDate){
        swal({   title: "خطایی رخ داده است!",   text: "آخرین تاریخ اعتبار استعلام میبایست قبل از آغاز عملیات باشد.",
                type: "warning",   showCancelButton: false,   confirmButtonColor: "#DD6B55",
                confirmButtonText: "توضیح بیشتر",   closeOnConfirm: false   ,allowOutsideClick:	true },
            function(){
                var win = window.open("/سایر-خدمات/آموزش", '_blank');
                win.focus();
            });
        return false;
    }
}
